'use strict';
const http = require('http');
const Homey = require('homey');

const defaultPort = 8080;
const defaultPassword = '';

const deviceListPath = '/json.htm?type=devices';


class Domoticz{

    constructor(uname,password,host,port){
        this.uname = uname || '';
        this.password = password || this.defaultPassword;
        this.port = port || this.defaultPort;
        this.host = host;
        
    }

    // load domoticz from homey settings
    fromSettings(){

        let settings = Homey.ManagerSettings.get('domotics_config');
        if(settings) {
            return new Domoticz(settings.username, settings.password, settings.host, settings.port);
        }
        return null;
    }
    // get an 'unique' prefix to be used in the drivers
    getHostString(){
        return this.host+":"+this.port;
    }

    updateDevice(type,idx,command,passcode){
        let path = "/json.htm?type=command";
        path += "&param="+type;
        path += "&idx="+idx;

        switch(type){
            case 'setsetpoint':
                path += "&setpoint="+command;
                break;
            case 'switchlight':
                path += "&switchcmd="+command;
                break;
        }

        if(passcode){
            path += "&passcode="+passcode;
        }
        Homey.app.doLog("Command url to execute: ");
        Homey.app.doLog(path);

        return new Promise((resolve,reject)=>{
            this._makeHttpRequest(path).then((data)=>{
                Homey.app.doLog("Update command result");
                Homey.app.doLog(data.body);
                let body = JSON.parse(data.body);
                if(body.status === 'OK'){
                    return resolve(true);
                }
            }).catch((error)=>{
                return reject(error);
            });
        });

    }

    // retrieve the first device of the given type
    findDevice(type,subtype,id){
        Homey.app.doLog("Start searching devices");

        let path = deviceListPath;
        if(id){
            let path = deviceListPath + "&rid="+id;
        }

        return new Promise((resolve,reject)=>{
            this._makeHttpRequest(deviceListPath).then(
                (data)=>{
                    let body = JSON.parse(data.body);
                    Homey.app.doLog("Found data in domoticz");

                    if(body.status === "OK"){

                        let i = 0; 
                        let needle = null;

                        if(type == null && subtype == null){

                            return resolve(body.result);
                        }

                        
                        throw Error('No device found!');
                        
                    }else{
                        Homey.app.doError("Invalid status received from data");
                        throw Error('invalid state from domoticz');
                    }
                    
                }).catch((error)=>{
                    Homey.app.doError('Error while connecting??');
                    this.authToken = null;
                    return reject(error);
                });
        });



        
    }

    doLogin(){
        Homey.app.doLog("Start logging into system");

        return new Promise((resolve,reject)=>{
            this._makeHttpRequest(deviceListPath).then(
                (data)=>{
                    if(data.body.status ==='OK'){
                        return resolve(true);
                    }else{
                        return reject(false);
                    }
                    
                }).catch((error)=>{
                    Homey.app.doError('Error while logging into system??');
                    this.authToken = null;
                    return reject(error);
                });
        });
    }


    _makeHttpRequest(path){
        Homey.app.doLog("Start creating a request");
        this.authToken = new Buffer(this.uname+":"+this.password).toString('base64');
        Homey.app.doLog("username: "+this.username);
        Homey.app.doLog("password: "+this.password);
        Homey.app.doLog("Host: "+this.host);
        Homey.app.doLog("Port: "+this.port);
        Homey.app.doLog("path: "+path);
        Homey.app.doLog("Authtoken: "+this.authToken);

        return new Promise((resolve,reject)=>{
            const req  = http.get({
                host: this.host,
                port: this.port,
                path: path,
                headers: {
                  'Content-length': 0,
                  'Authorization': 'Basic '+this.authToken  
                },
            },(res)=>{

                var body = '';
                res.on('data',(chunk)=>{
                    Homey.app.doLog("retrieve data");
                    body+=chunk;
                });
                res.on('end',()=>{
                    Homey.app.doLog("Done retrieval of data");
                    res.body = body;
                    resolve(res);
                });
            }).on('error',(err)=>{
                Homey.app.doLog("Error while connecting to domoticz");
                Homey.app.doLog(err);
                reject(err);
            });

            
        });
    }

}

module.exports = Domoticz;